			$(document).ready(function(){
				$.datepicker.setDefaults(
					{
						dateFormat: 'm/d/yy',
						dayNames: ['Sunday','Monday','Tuesday','Wednesday','Thursday','Friday','Saturday'],
						dayNamesShort: ['Sun','Mon','Tue','Wed','Thu','Fri','Sat'],
						dayNamesMin: ['Su','Mo','Tu','We','Th','Fr','Sa'],
						monthNames: ['January','February','March','April','May','June','July','August','September','October','November','December'],
						monthNamesShort: ['Jan','Feb','Mar','Apr','May','Jun','Jul','Aug','Sep','Oct','Nov','Dec'],
						firstDay: 0
                    }
				);
			});

div.script:
$(document).ready(function()
{
	App.Search.projectID = 1;

	//search bar keyboard behaviour
	$('#search_query').on('keydown', function(e) {
		if (e.key === "Tab" || e.keyCode === 9) {
			if($('#top_search_dialog').is(':visible') && !e.shiftKey) {
				$('.search-categories').find('.dialog-category').first().focus();
			}
			else {
				if(e.shiftKey) {
					if ($('.dynamic_add').length) {
						$('.dynamic_add').focus();
					}
					else {
						$('.link-noline.text-wrap-ellipsis.link-tooltip').focus();
					}
					if($('#top_search_dialog').is(':visible')) {
						App.Search.hideDialog();
					}
				}
				else {
					if(!$('#top_search_dialog').is(':visible')) {
						$('#inProgressLink').focus();
					}
				}

			}
		}
		else {
			if(!$('#top_search_dialog').is(':visible') && !e.shiftKey) {
				App.Search.showDialog();
			}
		}
	});
});

ul.script:
	$(document).ready(function() {
		App.Controls.triggerClickOnEnter('#navigation-suites');	
		App.Controls.triggerClickOnEnter('#navigation-runs');	
		App.Controls.triggerClickOnEnter('#navigation-milestones');	
		App.Controls.triggerClickOnEnter('#none');	
	});

	var currDropdownId = null, currDropdownBtn = null;
	document.addEventListener("DOMContentLoaded", function() {
		const overviewButtons = document.querySelectorAll('.overview-button-selected');
		overviewButtons.forEach(function (overviewButton) {
			const dropdownParentId = overviewButton.getAttribute('parent-id');
			const dropdownNavSection = document.querySelector("#"+dropdownParentId+"-nav");
			var dropdownNavButton = document.querySelector("."+dropdownParentId);
			dropdownNavButton.parentNode.classList.add('main-parent-menu-item-selected');
			dropdownNavSection.style.backgroundColor = '#F6F9FE';
			dropdownNavSection.style.paddingBottom = '12px';
		});

		var dropdowns = document.querySelectorAll('.sidebar-drop-down-list[data-active-dropdown]');
		dropdowns.forEach(function (dropdown) {
			var links = dropdown.querySelectorAll("li.main-sidebar-menu-item-selected a");
			if (links.length > 0) {
				dropdown.style.display = "flex";
				dropdown.classList.add('main-sidebar-nested-menu')

				var dropdownId = dropdown.getAttribute('id');
				currDropdownId = dropdownId;
				currDropdownBtn = links[0].getAttribute('id');
				var dropdownNavButton = document.querySelector("."+dropdownId);
				dropdownNavButton.parentNode.classList.add('main-parent-menu-item-selected');
				const dropdownNavSection = document.querySelector("#"+dropdownId+"-nav");
				dropdownNavSection.style.backgroundColor = '#F6F9FE';
				dropdownNavSection.style.paddingBottom = '12px';
			}
		});
	});

	function setPopoverPosition() {
		const sidebarHeader = document.getElementById("sidebar-tree-header");
		const popup = document.getElementById("expand-collapse-container");
		const rect = sidebarHeader.getBoundingClientRect();

		const popupTop = rect.top - popup.offsetHeight - 54;

		popup.style.top = popupTop + "px";
	}

	function toggleSectionOverview(selected, dropdown_id, element_id) {
		const dropdownElement = document.querySelector('#'+dropdown_id);
		if(!selected) {
			const currentDisplay = dropdownElement.style.display;
			dropdownElement.style.display = currentDisplay === 'none' ? 'block' : 'none';
			document.querySelector('#'+element_id).classList.toggle('overview-collapsed');
			setPopoverPosition();
		} 
	}
	
	/*
	function toggleDropdown(dropdown_id, dropdown_btn_id, dropdown_btn_class) {
