input.script:
function updateSelectionCounts(newAttachments) {
  let count = $(newAttachments).val().split(',').filter(Number).length;
  if ($('#selectedAttachmentCount').text() !== 'All') {
    $('#selectedAttachmentCount').text(count.toString());
    $('#selectedAttachmentCountDelete').text(count > 0 ? `(${count.toString()})` : '');
  }
}
$(document).ready(function()
{
	App.Attachments.orderByOptions = {"created_on":"Date","name":"Name","size":"Size"};
	$('#libraryAddAttachment').on('keydown', function(e) {
		if (e.key === "Enter" || e.keyCode === 13) {
			this.click();
		}
	});
	$('#attachments-library-sortby').on('keydown', function(e) {
		if (e.key === "Enter" || e.keyCode === 13) {
			e.preventDefault();
			$(this).click();
		}
	});
	App.Controls.triggerClickOnEnter('#libraryDeleteAttachment', true);
	App.Controls.triggerClickOnEnter('#attachmentFilterByChange');
	App.Controls.triggerClickOnEnter('.dropdown-menu-link');
	App.Controls.triggerClickOnEnter('.link-noline');

	App.Controls.handleShiftTabDropdown('#attachments-library-sortby', '#sortDropdown', App.Dropdowns.closeAll);
	App.Controls.handleFirstElementTabIn('#attachments-library-sortby', '#sortDropdown');
	App.Controls.handleLastElementTabOut('#attachments-library-sortby', '#sortDropdown', App.Dropdowns.closeAll);

	App.Controls.handleFirstElementTabIn('#attachmentFilterByChange', '#filterAttachmentsContent');

	var defaultTimeout = null;
	$('#libraryAttachmentsSearch').on('keyup', function() {
		if (defaultTimeout != null) clearTimeout(defaultTimeout);
		defaultTimeout = setTimeout(
			function() {
				defaultTimeout = null;
				App.Attachments.reloadRepository();
			},
			500
		);
	});

