div.script:
    function expandCollapseTreeHeight() {
        const sidebarTreeHeaderWrapper = document.querySelector('.sidebar-tree-header-wrapper');
        const sidebarTreeHeader = document.querySelector('.sidebar-tree-header');
        const sidebarTreeToolbar = document.querySelector('#sidebarToolbar');
        const sidebarContent = document.querySelector('.sidebar-content');
        const sidebarRunsTreeWrapper = document.querySelector('.runs-tree-wrap');
        const expandButton = document.querySelector('.sidebar-tree-expand');
        const collapseButton = document.querySelector('.sidebar-tree-collapse');
        const isExpanded = sidebarTreeHeader.classList.contains('active');

        if(isExpanded) {
            $('.sidebar-layout-content').slideDown();
            sidebarTreeHeader.classList.remove('active');
            expandButton.classList.remove('hidden');
            collapseButton.classList.add('hidden');
            
            sidebarTreeHeaderWrapper.style.removeProperty('position');
            sidebarTreeHeaderWrapper.style.removeProperty('top');
            sidebarTreeHeaderWrapper.style.removeProperty('padding-top');
            sidebarTreeToolbar.style.removeProperty('position');
            sidebarTreeToolbar.style.removeProperty('top');
            sidebarRunsTreeWrapper.style.removeProperty('padding-top');
            sidebarContent.scrollTop = 0;
        } else {
            $('.sidebar-layout-content').slideUp();
            sidebarTreeHeader.classList.add('active');
            expandButton.classList.add('hidden');
            collapseButton.classList.remove('hidden');
            $('#expand-collapse-container').hide();
            
            sidebarTreeHeaderWrapper.style.position = 'sticky';
            sidebarTreeHeaderWrapper.style.zIndex = '10';
            sidebarTreeHeaderWrapper.style.top = '39px';
            sidebarTreeToolbar.style.position = 'sticky';
            sidebarTreeToolbar.style.top = '70px';
            sidebarRunsTreeWrapper.style.paddingTop = '0px';
        }
    }

