div.script:
	const dropdownContent = document.getElementById('displayDropdown');
	const anchorButton = document.querySelector('#sidebarToolbar .toolbar-dropdown');
	const sidebar = document.querySelector('.sidebar-content');
	const treeHeader = document.querySelector('.sidebar-tree-header-wrapper');

	anchorButton.addEventListener('click', function () {

		// if already open then close it
		if(dropdownContent.style.display === 'block'){
			dropdownContent.style.display = 'none';
			return;
		}

		// Get the computed style of the element
		const computedStyle = window.getComputedStyle(treeHeader);

		// Check if the position property is sticky
		const position = computedStyle.getPropertyValue('position');
		const anchorButtonRect = anchorButton.getBoundingClientRect();
		var noticeOffset = $('#notice').outerHeight() || 0;
		if (position !== 'sticky') {
			// since header height is 44px
			dropdownContent.style.top = (anchorButtonRect.bottom + sidebar.scrollTop - 44 - noticeOffset) + 'px';
			dropdownContent.style.left = anchorButtonRect.left + 'px';
		} else {
			dropdownContent.style.top = 32 + 'px';
			dropdownContent.style.left = 5 + 'px';
		}
		dropdownContent.style.display = 'block';
	});

	$(document).ready(function() {
		App.Controls.triggerClickOnEnter('.dropdown-header-order-by');
		App.Controls.triggerClickOnEnter('.dropdown-header-group-by');
		App.Controls.triggerClickOnEnter('.dropdown-option-value');
		
		App.Controls.handleShiftTabDropdown('.dropdown-header-order-by', '.dropdown-options-order-by', closeDropdown);
		App.Controls.handleFirstElementTabIn('.dropdown-header-order-by', '.dropdown-options-order-by');
		App.Controls.handleLastElementTabOut('.dropdown-header-order-by', '.dropdown-options-order-by', closeDropdown);
		
		App.Controls.handleShiftTabDropdown('.dropdown-header-group-by', '.dropdown-options-group-by', closeDropdown);
		App.Controls.handleFirstElementTabIn('.dropdown-header-group-by', '.dropdown-options-group-by');
		App.Controls.handleLastElementTabOut('.dropdown-header-group-by', '.dropdown-options-group-by', closeDropdown);
		
		function closeDropdown() {
			if($('.dropdown-options-group-by').is(':visible')) {
				$('.dropdown-header-group-by').click();
			}
			else if($('.dropdown-options-order-by').is(':visible')) {
				$('.dropdown-header-order-by').click();
			}
		}
    });
// JavaScript code
document.addEventListener("DOMContentLoaded", function () {
  const customDropdowns = document.querySelectorAll(".custom-dropdown");

  customDropdowns.forEach(function (dropdown) {
    const header = dropdown.querySelector(".dropdown-header");
    const options = dropdown.querySelector(".dropdown-options");

    // Toggle the active class to show/hide options
    header.addEventListener("click", function () {
      dropdown.classList.toggle("active");
	  if(dropdown.classList.contains("active")) {
		dropdown.classList.remove("upward-dropdown");
		dropdown.classList.remove("downward-dropdown");
		// If dropdown options height is more than screen height in that case will open the dropdown
		// in upwards direction otherwise it will open downwards direction.
		if(options.getBoundingClientRect().height + header.getBoundingClientRect().bottom + 10 > window.innerHeight) {
			dropdown.classList.add("upward-dropdown");
		} else {
			dropdown.classList.add("downward-dropdown");
		}
	  }
    });

    // Handle option selection
    options.addEventListener("click", function (event) {
      const selectedOption = event.target;
      const value = selectedOption.getAttribute("data-value");

      // Remove "active" class from previously selected option
      const activeOption = options.querySelector(".active");
      if (activeOption) {
        activeOption.classList.remove("active");
		dropdown.classList.remove("upward-dropdown");
		dropdown.classList.remove("downward-dropdown");
      }

      // Add "active" class to the newly selected option
      selectedOption.classList.add("active");

      header.querySelector("span").textContent = selectedOption.textContent;
      dropdown.classList.remove("active");
    });
  });

  // Close dropdowns and remove "rotate-90" class when clicking outside
  document.addEventListener("click", function (event) {
    customDropdowns.forEach(function (dropdown) {
      if (!dropdown.contains(event.target)) {
        dropdown.classList.remove("active");
		dropdown.classList.remove("upward-dropdown");
		dropdown.classList.remove("downward-dropdown");
        
        // Remove "rotate-90" class to reset the arrow
        const arrow = dropdown.querySelector(".dropdown-arrow"); // Change this line to target the  element
      }
    });
  });
});

    const positionFilterBubble = () => {
		const contentInner = $('#content-inner');
		const contentInnerWidth = contentInner.outerWidth();
		const MAX_WIDTH_TO_SHOW_TWO_COLUMNS = 750;

		const descriptionHeight = $('#description').height();
		const margin = descriptionHeight > 0 ? 21 : 0;

		const contentHeaderInnerHeight = $('.content-header-inner').outerHeight();
		const contentHeaderHeight = $('#content-header').outerHeight();

		const successMessageHeight = $('.message-success').outerHeight();
		const successMessageMargin =  successMessageHeight > 0 ? 17.5 : 0;

		// calculating top offset as per screen size
		const filterTopOffset = (contentInnerWidth < MAX_WIDTH_TO_SHOW_TWO_COLUMNS ? 386 : 326) + contentHeaderInnerHeight + successMessageHeight + successMessageMargin;
		if(!$('#filterTestsBubble').hasClass('report_bubble')){
			contentInner.find('#filterTestsBubble').css('top', filterTopOffset);
		}
		const caseTopOffset = 66 + descriptionHeight + margin + contentHeaderHeight + successMessageHeight + successMessageMargin;
		const filterCasesBubble = contentInner.find('#filterCasesBubble');
		if(!$('#filterCasesBubble').hasClass('reports-filter-bubble')){
			filterCasesBubble.css('top', caseTopOffset);
		}
	}
    function adjustHeaderPosition() {
        const $header = $('#top');
        if ($(window).width() < 960) {
          let scrollLeft = $(window).scrollLeft();
          if($header.outerWidth() - $(window).width()  >= scrollLeft) {
              $header.css('left', -scrollLeft);
          }
        } else {
          $header.css('left', '0');
        }
    }
    $(window).on('scroll resize', function() {
        adjustHeaderPosition();
    });
    $(window).ready(function() {
        adjustHeaderPosition();
    })
    $(window).resize(function() {
        setTimeout(positionFilterBubble, 10);
    });
	
