                    html2canvas($('#statusChartContainer')[0], {
                        height: 230,
                        scrollY: -window.scrollY
                    }).then(function(canvas) {
                        var dl = document.getElementById('dl');
                        var dt = canvas.toDataURL('image/png');
                        dl.href = dt;
                        dl.click();
                    });
                    App.Charts.status.setChartAttribute('enableSlicing', '1');
                }, 100);
            });
        });
    });

