    var isFilter = 0;

    $('#filterTestsContent, #exportCsvSectionsSelection').on('click', function() {
        isFilter = 1;
    });

    $('body').on('click', '#filterTestsCancel, #filterTestsApply, .button-cancel', function() {
        isFilter = 0;
    });

    $('body').on('keyup', function(e) {
        if (!$(e.target).is('input')  && !$(e.target).hasClass('fr-element fr-view') ) {
            var DKey = 68;
            var EKey = 69;
            var RKey = 82;
            var PKey = 80;
            var MKey = 77;
            var AKey = 65;
            var JKey = 74;
            var KKey = 75;
            var QKey = 81;

            if ($('#dialog-ident-exportCsvDialog').css('display') === 'none') {
                isFilter = 0;
            }

            if (e.which === DKey && !e.ctrlKey && isFilter === 0) {
                if (!$(e.target).hasClass('form-control-full') && !$(e.target).attr('contenteditable')) {
                                        App.Defects.preparePush(1, 0, 'runs/view', 3648);
                                    }
            }

                        if (e.which === EKey && isFilter === 0) {
                if (!$(e.target).hasClass('form-control-full') && !$(e.target).attr('contenteditable')) {
                    App.Page.load('runs/edit/3648/1');
                }
            }

            if (e.which === RKey && !e.ctrlKey) {
                if (!$(e.target).hasClass('form-control-full') && !$(e.target).attr('contenteditable')) {
                    App.Tests.massAddResult(1);
                }
            }

            if (e.which === PKey) {
                if (!$(e.target).hasClass('form-control-full') && !$(e.target).attr('contenteditable')) {
                    if (App.QPane.isVisible()) {
                        App.Runs.passRowAndNext(
                            App.QPane.getCurrentRowID()
                        );
                    }
                }
            }

            if (e.which === MKey) {
                if (!$(e.target).hasClass('form-control-full') && !$(e.target).attr('contenteditable')) {
                    if (App.QPane.isVisible()) {
                        App.Tests.addCommentNoDialogActivate(
                            App.QPane.getCurrentRowID()
                        );
                    }
                }
            }

            if (e.which === AKey) {
                if (!$(e.target).hasClass('form-control-full') && !$(e.target).attr('contenteditable')) {
                    App.Tests.massAssignTo();
                }
            }
            
            if (e.which === JKey) {
                if (!$(e.target).hasClass('form-control-full') && !$(e.target).attr('contenteditable')) {
                    if (App.QPane.isVisible()) {
                        App.Runs.nextRow();
                    }
                }
            }

            if (e.which === KKey) {
                if (!$(e.target).hasClass('form-control-full') && !$(e.target).attr('contenteditable')) {
                    if (App.QPane.isVisible()) {
                        App.Runs.prevRow();
                    }
                }
            }

            if (e.which === QKey) {
                if (!$(e.target).hasClass('form-control-full') && !$(e.target).attr('contenteditable')) {
                    if (!App.Ajax.isBusy()) {
                        App.QPane.tryToggleRow();
                    }
                }
            }
        }
    });

