        function shuffleArray(array) {
            for (let i = array.length - 1; i > 0; i--) {
                const j = Math.floor(Math.random() * (i + 1));
                [array[i], array[j]] = [array[j], array[i]];
            }
            return array;
        }

        document.addEventListener('DOMContentLoaded', function() {
            const container = document.getElementById('partner-cards');
            
            // Filter partners with display: true
            const displayedPartners = partners.filter(partner => partner.display);
            
            // Shuffle the displayed partners
            const shuffledPartners = shuffleArray(displayedPartners);
            
            // Select up to 3 partners (or fewer if there aren't 3 displayed partners)
            const selectedPartners = shuffledPartners.slice(0, 1);
            
            selectedPartners.forEach(partner => {
                container.innerHTML += createPartnerCard(partner);
            });
        });

